import { useState } from "react";
import { useNavigate } from "react-router-dom"; // Pour la navigation dans React.js
import axios from "axios";
import "./Forgetpassword.css"; // Import du fichier CSS

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); // Utilisation de useNavigate

  const isValidEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const handleForgotPassword = async () => {
    if (!email) {
      alert("Erreur : Veuillez entrer votre adresse e-mail");
      return;
    }

    if (!isValidEmail(email)) {
      alert("Erreur : Veuillez entrer une adresse e-mail valide");
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.post(
        "http://localhost:5001/auth/forgot-password", // Mettez l'URL de votre API
        { email }
      );
      alert("Succès : " + response.data.message);

      // Supposons que le backend renvoie le token
      const token = response.data.token; // Assurez-vous que le backend renvoie un token
      if (token) {
        navigate(`/reset-password/${token}`); // Naviguer vers la page de réinitialisation avec le token
      }

      setEmail(""); // Réinitialiser l'email
    } catch (error) {
      alert(
        "Erreur : " +
          (error.response?.data?.message || "Une erreur est survenue")
      );
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <h2 className="title">Mot de passe oublié</h2>
      <input
        className="input"
        placeholder="Entrez votre adresse e-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type="email"
        required // Ajoutez required pour une meilleure validation
      />
      <button
        className={`button ${isLoading ? "buttonDisabled" : ""}`}
        onClick={handleForgotPassword}
        disabled={isLoading}
      >
        {isLoading ? "Chargement..." : "Réinitialiser le mot de passe"}
      </button>
    </div>
  );
};

export default ForgotPassword;
