import React, { useEffect, useState } from "react";
import './AccountSettings.css';
import loginImage from '../../pages/user-profile-icon-symbol-template-free-vector.jpg';

const AccountSettings = () => {
    const [user, setUser] = useState(null);
    const [error, setError] = useState("");
    const [email, setEmail] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    useEffect(() => {
        const storedEmail = localStorage.getItem("userEmail");
        console.log("üîç Email r√©cup√©r√© depuis localStorage :", storedEmail);

        if (!storedEmail) {
            setError("üö® Aucun email trouv√©. Veuillez vous reconnecter.");
        } else {
            setEmail(storedEmail);
        }
    }, []);

    useEffect(() => {
        if (!email) return;

        const fetchUserByEmail = async () => {
            try {
                console.log("üîÑ Requ√™te en cours pour r√©cup√©rer l'utilisateur...");
                const response = await fetch(`http://localhost:5001/user/email/${email}`);

                if (!response.ok) {
                    throw new Error(`Erreur HTTP : ${response.status} - ${response.statusText}`);
                }

                const data = await response.json();
                console.log("‚úÖ Donn√©es utilisateur r√©cup√©r√©es :", data);

                if (data.success && data.user) {
                    console.log("üì∏ Image r√©cup√©r√©e :", data.user.image);
                    setUser(data.user);
                } else {
                    throw new Error("Les donn√©es utilisateur sont invalides.");
                }
            } catch (err) {
                setError("Erreur lors de la r√©cup√©ration des informations utilisateur.");
                console.error("‚ùå D√©tails de l'erreur :", err);
            }
        };

        fetchUserByEmail();
    }, [email]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUser((prevUser) => ({
            ...prevUser,
            [name]: value,
        }));
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                console.log("üì∏ Image convertie en Base64 :", reader.result);
                setUser((prevUser) => ({
                    ...prevUser,
                    image: reader.result, // Stocke l‚Äôimage en Base64
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSaveChanges = async () => {
        if (!user) return;

        console.log("üîÑ Enregistrement des modifications :", user);

        try {
            const response = await fetch(`http://localhost:5001/user/email/${email}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    name: user.name,
                    role: user.role,
                    image: user.image, 
                }),
            });

            const data = await response.json();
            if (response.ok) {
                setSuccessMessage("‚úÖ Modifications enregistr√©es avec succ√®s !");
                console.log("‚úÖ Mise √† jour r√©ussie :", data);
            } else {
                setError("üö® √âchec de la mise √† jour de l'utilisateur.");
            }
        } catch (err) {
            setError("‚ùå Erreur lors de la mise √† jour de l'utilisateur.");
            console.error(err);
        }
    };

    return (
      <div className='accountsettings'>
        <h1 className='mainhead1'>Personal Information</h1>

        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}

        {/* üìå Image de profil avec upload */}
        <div className="profile-image-container">
            <img 
                src={
                    user?.image 
                        ? user.image.startsWith("data:image") 
                            ? user.image 
                            : `http://localhost:5001/uploads/${user.image}`
                        : loginImage
                } 
                alt="Profile" 
                className="profile-image" 
            />
        </div>

        <input 
            type="file" 
            accept="image/*" 
            onChange={handleImageUpload} 
            className="upload-input"
        />

        <div className='form'>
          <div className='form-group'>
            <label htmlFor='name'>Your Name <span>*</span></label>
            <input 
              type='text' 
              name='name' 
              id='name' 
              value={user?.name || ''} 
              onChange={handleInputChange} 
              disabled={!user} 
            />
          </div>

          <div className='form-group'>
            <label htmlFor='role'>Role <span>*</span></label>
            <input 
              type='text' 
              name='role' 
              id='role' 
              value={user?.role || ''} 
              onChange={handleInputChange} 
              disabled={!user}
            />
          </div>

          <div className='form-group'>
            <label htmlFor='email'>Email <span>*</span></label>
            <input 
              type='email' 
              name='email' 
              id='email' 
              value={user?.email || ''} 
              disabled
            />
          </div>
        </div>

        <button 
          className='mainbutton1' 
          onClick={handleSaveChanges} 
          disabled={!user} 
        >
          Save Changes
        </button>
      </div>
    );
};

export default AccountSettings;
