import React, { useState } from "react";
import axios from "axios";

const ChangePassword = () => {
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");
    const [error, setError] = useState("");

    // Fonction pour changer le mot de passe
    const handleChangePassword = async () => {
        if (!oldPassword || !newPassword || !confirmPassword) {
            setError("Tous les champs sont obligatoires.");
            return;
        }

        if (newPassword !== confirmPassword) {
            setError("Les nouveaux mots de passe ne correspondent pas.");
            return;
        }

        try {
            const token = localStorage.getItem("token"); // Récupère le token JWT du localStorage
            if (!token) {
                setError("Vous devez être connecté pour changer votre mot de passe.");
                return;
            }

            const response = await axios.put(
                "http://localhost:5001/auth/change-password", // Pas besoin d'ID dans l'URL
                { oldPassword, newPassword },
                {
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`, // Envoie le token JWT dans le header
                    },
                }
            );

            setMessage(response.data.message);
            setError("");

            // Réinitialiser les champs
            setOldPassword("");
            setNewPassword("");
            setConfirmPassword("");
        } catch (err) {
            console.error("Erreur lors de la requête:", err.response?.data || err.message);
            setError(err.response?.data?.message || "Une erreur s'est produite.");
        }
    };

    return (
        <div className="accountsettings">
            <h1 className="mainhead1">Changer le mot de passe</h1>

            {error && <p style={{ color: 'red' }}>{error}</p>}
            {message && <p style={{ color: 'green' }}>{message}</p>}

            <div className="form">
                <div className="form-group">
                    <label htmlFor="oldpass">Ancien mot de passe <span>*</span></label>
                    <input
                        type="password"
                        id="oldpass"
                        value={oldPassword}
                        onChange={(e) => setOldPassword(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="newpass">Nouveau mot de passe <span>*</span></label>
                    <input
                        type="password"
                        id="newpass"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="confirmpass">Confirmer le mot de passe <span>*</span></label>
                    <input
                        type="password"
                        id="confirmpass"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </div>
            </div>

            <button className="mainbutton1" onClick={handleChangePassword}>
                Enregistrer les modifications
            </button>
        </div>
    );
};

export default ChangePassword;
