import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, Alert } from 'react-native';
import axios from 'axios';
import './ResetPassword.css'; // Assurez-vous que le chemin est correct

const ResetPassword = ({ navigation, route }) => {
  const [newPassword, setNewPassword] = useState('');
  const { token } = route.params; // Le token est passé via les paramètres de navigation

  const handleResetPassword = async () => {
    try {
      const response = await axios.post('http://votre-api.com/reset-password', { token, newPassword });
      Alert.alert('Succès', response.data.message);
      navigation.navigate('Login'); // Rediriger vers la page de connexion
    } catch (error) {
      Alert.alert('Erreur', error.response?.data?.message || 'Une erreur est survenue');
    }
  };

  return (
    <View className="container">
      <Text className="title">Réinitialiser le Mot de Passe</Text>
      <TextInput
        placeholder="Nouveau Mot de Passe"
        value={newPassword}
        onChangeText={setNewPassword}
        secureTextEntry
        className="input"
      />
      <TouchableOpacity onPress={handleResetPassword}>
        <Text>Réinitialiser le Mot de Passe</Text>
      </TouchableOpacity>
    </View>
  );
};

export default ResetPassword;
