import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // Import the styles
import "./Forgetpassword.css";

const ForgetPassword = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5001/auth/forgot-password", { email });
      toast.success(response.data.message); // Display success message
      setEmail(""); // Clear the input field
    } catch (error) {
      // Check if the error has a response and display the message accordingly
      if (error.response && error.response.data) {
        toast.error(error.response.data); // Display server error message
      } else {
        toast.error("Erreur lors de l'envoi du lien de réinitialisation."); // Fallback message
      }
    }
  };

  return (
    <div className="form-container">
      <h2>Réinitialiser le Mot de Passe</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Entrez votre email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Envoyer</button>
      </form>
      <ToastContainer /> {/* Toast container for displaying toasts */}
    </div>
  );
};

export default ForgetPassword;
