import React, { useState } from 'react';
import axios from 'axios';

const VerificationComponent = ({ confirmationCode }) => {
  const [isVerified, setIsVerified] = useState(false);
  const [error, setError] = useState(null);

  const handleVerify = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/verify', {
        confirmationCode,
      });
      if (response.data.success) {
        setIsVerified(true);
      } else {
        setError(response.data.message);
      }
    } catch (err) {
      setError('Erreur lors de la vérification. Veuillez réessayer.');
      console.error(err);
    }
  };

  return (
    <div>
      {isVerified ? (
        <h2>Votre compte a été vérifié avec succès !</h2>
      ) : (
        <div>
          <h2>Vérifiez votre compte</h2>
          <p>Cliquez sur le bouton ci-dessous pour activer votre compte.</p>
          <button onClick={handleVerify}>Vérifier mon compte</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}
    </div>
  );
};

export default VerificationComponent;
