const handleChangePassword = async () => {
    if (!oldPassword || !newPassword || !confirmPassword) {
        setError("Tous les champs sont obligatoires.");
        return;
    }

    if (newPassword !== confirmPassword) {
        setError("Les nouveaux mots de passe ne correspondent pas.");
        return;
    }

    try {
        const userId = "ID_UTILISATEUR"; // Assure-toi que cet ID est bien récupéré

        console.log("Envoi de la requête avec userId:", userId); // Vérifier si userId est correct

        const response = await axios.put(`http://localhost:5001/auth/change-password/${userId}`, {
            oldPassword,
            newPassword,
        });

        console.log("Réponse du serveur:", response.data); // Voir la réponse du serveur

        setMessage(response.data.message);
        setError("");
    } catch (err) {
        console.error("Erreur axios:", err.response?.data || err.message);
        setError(err.response?.data?.message || "Une erreur s'est produite.");
    }
};
