import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom"; // React Router pour la navigation et récupérer le token
import axios from "axios";
import "./Resetpassword.css"; // Vérifiez que ce fichier existe bien

const ResetPassword = () => {
  const { token } = useParams(); // Récupérer le token depuis l'URL
  const [newPassword, setNewPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false); // Ajouter un état de chargement
  const navigate = useNavigate(); // Utiliser useNavigate() pour rediriger

  const handleResetPassword = async () => {
    if (!newPassword) {
      alert("Erreur : Veuillez entrer un nouveau mot de passe");
      return;
    }

    setIsLoading(true); // Indiquer que le chargement a commencé
    try {
      const response = await axios.post("http://localhost:5001/auth/reset-password", { // Mettez l'URL de votre API
        token,
        newPassword,
      });

      alert("Succès : " + response.data.message);
      navigate("/login"); // Rediriger vers la page de connexion
    } catch (error) {
      alert(
        "Erreur : " +
          (error.response?.data?.message || "Une erreur est survenue")
      );
    } finally {
      setIsLoading(false); // Indiquer que le chargement est terminé
    }
  };

  return (
    <div className="container">
      <h2 className="title">Réinitialiser le Mot de Passe</h2>
      <input
        type="password"
        className="input"
        placeholder="Nouveau Mot de Passe"
        value={newPassword}
        onChange={(e) => setNewPassword(e.target.value)}
        required // Ajoutez required pour une meilleure validation
      />
      <button className="button" onClick={handleResetPassword} disabled={isLoading}>
        {isLoading ? "Chargement..." : "Réinitialiser le Mot de Passe"}
      </button>
    </div>
  );
};

export default ResetPassword;
