import React from 'react'

const handleChangePassword = async () => {
    if (newPassword !== confirmPassword) {
        setError("Les nouveaux mots de passe ne correspondent pas.");
        return;
    }

    try {
        const userId = "ID_UTILISATEUR"; // Remplace par le bon ID utilisateur

        const response = await axios.put(`http://localhost:5001/auth/change-password/${userId}`, {
            oldPassword,
            newPassword,
        });

        setMessage(response.data.message);
        setError("");
    } catch (err) {
        setError(err.response?.data?.message || "Une erreur s'est produite.");
    }
};

const ChangePassword = () => {
    return (
        <div className='accountsettings'>
            <h1 className='mainhead1'>Change Password</h1>

            <div className='form'>
                <div className='form-group'>
                    <label htmlFor='oldpass'>Old Password <span>*</span></label>
                    <input type="password"
                    />
                </div>

                <div className='form-group'>
                    <label htmlFor='newpass'>New Password <span>*</span></label>
                    <input type="password"
                    />
                </div>


            </div>

            <button className='mainbutton1'

            >Save Changes</button>
        </div>
    )
}

export default ChangePassword
