import React, { useState } from "react";
import axios from "axios";
import "./Forgetpassword.css";

const ForgetPassword = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5001/auth/forgot-password", { email });
      setMessage(response.data.message);
    } catch (error) {
      // Check if the error has a response and set the message accordingly
      if (error.response && error.response.data) {
        setMessage(error.response.data); // Display the server error message
      } else {
        setMessage("Erreur lors de l'envoi du lien de réinitialisation."); // Fallback message
      }
    }
  };

  return (
    <div className="form-container">
      <h2>Réinitialiser le Mot de Passe</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Entrez votre email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Envoyer</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default ForgetPassword;
